export const pages = [{"id":"about.json","url":"about","title":"About","contents":"\n{\n  \"pageName\":\"About\",\n  \"pageURL\":\"about\",\n  \"heading\":\"About\"\n\n}"},{"id":"build.json","url":"build-your-smart-forest","title":"Build Your Smart Forest","contents":"\n{\n  \n  \"pageName\":\"Build Your Smart Forest\",\n\n  \"pageURL\":\"build-your-smart-forest\"\n}"},{"id":"contact.json","url":"contact","title":"Contact Us","contents":"\n\n{\n  \"pageName\":\"Contact Us\",\n  \"pageURL\":\"contact\",\n  \"title\": \"Contact Us\",\n  \"name\": \"Canada's Forest Trust\",\n  \"address\": \"Street Address, Ottawa, Ontario, CANADA\",\n  \"additionalInfo\": \"info tbd\",\n  \"contactSuccess\": \"Thank you for reaching out.\"\n}"},{"id":"home.json","url":"","title":"Canada's Forest Trust - Home","contents":"\n{\"pageName\":\"Canada's Forest Trust - Home\",\n  \"pageURL\":\"\",\n  \"title\": \"Canada's Forest Trust\",\n  \"home\": \"HOME\",\n  \"menu1\": \"INTRO\",\n  \"menu2\": \"WHAT\",\n  \"menu3\": \"HOW\",\n  \"menu4\": \"WHO\",\n  \"menu5\": \"YOU\",\n  \"menu6\": \"CALCULATE\",\n  \"part1_header1\": \"Simply planting trees won’t save our planet.\",\n  \"part1_header2\": \"Building forests will.\",\n  \"part1_header3\": \"With your help, we will reforest 10 million acres of Canadian land and protect it forever.\",\n  \"part1_header4\": \"Canada's Forest Trust\",\n  \"part1_box1para\": \"**Recognize** that we need bold, long-term solutions to reverse the damage caused by deforestation and emissions in Canada\",\n  \"part1_box2para\": \"**Leverage** data-driven technologies and advanced analytics to support healthy, sustainable and biodiverse Smart Forests™\",\n  \"part1_box3para\": \"**Guarantee** protection and preservation through forest management practices that are based in science and designed for longevity\",\n  \"part1_button1\": \"Discover the Smart Forest initiative\",\n  \"part2_header1\": \"What is a Smart Forest?\",\n  \"part2_para1\": \"A Smart Forest combines the resilience of a natural ecosystem, developed over millennia, with the predictive capabilities of today’s technologies. Inside a Smart Forest, life thrives; outside of it, scientists, students, investors and conservationists use digital instruments to understand and activate the forest’s role in cleaning our water, purifying our air, and replenishing our lost resources.\",\n  \"part2_button1\": \"Learn more about Smart Forests\",\n  \"part3_header1\": \"How does a Smart Forest happen?\",\n  \"part3_para1\": \"To plant a tree, you need a shovel. To build a Smart Forest, you need a five-phase strategic action plan, validated by modern science and calibrated to address the needs of a healthy, thriving forest, now and for decades to come.\",\n  \"part3_button1\": \"Explore our Smart Forest action plan\",\n  \"part4_header1\": \"Who can support a Smart Forest?\",\n  \"part4_para1\": \"If you care about the future of our planet and the health of our natural ecosystems, you belong here. We invite youth, families, farmers, landowners, indigenous communities and Canada’s corporate leaders to collaborate on this resilient, long-term climate solution.\",\n  \"part5_header1\": \"I would like to...\",\n  \"part5_button1\": \"Meet my corporate ESG obligations\",\n  \"part5_button2\": \"Get my school involved\",\n  \"part5_button3\": \"Establish a legacy forest\",\n  \"part5_button4\": \"Contribute to a communal forest\",\n  \"part6_header1\": \"What difference can I make?\",\n  \"part6_para1\": \"Use our custom-built net-zero carbon calculator and forest calculator to understand exactly how your investment in Smart Forests will offset your climate impact.\",\n  \"part6_box1para1\": \"STEP 1\",\n  \"part6_box1para2\": \"What is your carbon footprint?\",\n  \"part6_box1para3\": \"Calculate how much carbon you, your family, your corporation, or your school generates in an average year.\",\n  \"part6_box1button1\": \"Calculate your carbon footprint\",\n  \"part6_box2para1\": \"STEP 2\",\n  \"part6_box2para2\": \"Your “net zero” Smart Forest target\",\n  \"part6_box2para3\": \"Calculate how many acres you, your family, your corporation, or your school must invest in to reach a net-zero emissions target. \",\n  \"part6_box2button1\": \"Calculate your NET ZERO target\",\n  \"part6_box3para1\": \"STEP 3\",\n  \"part6_box3para2\": \"Your “net negative” Smart Forest target\",\n  \"part6_box3para3\": \"Calculate how many acres you, your corporation, or your school must invest in to reach a net-negative emissions target.\",\n  \"part6_box3button1\": \"Calculate your NET NEGATIVE target\"\n}"},{"id":"portal.json","contents":"{\n  \"greeting\": \"Welcome Back,\",\n  \"subtitle\": \"\"\n}"},{"id":"what-is-sf.json","url":"what-is-a-smart-forest","title":"What Is a Smart Forest","contents":"\n{ \"pageName\":\"What Is a Smart Forest\",\n  \"pageURL\":\"what-is-a-smart-forest\",\n  \"learnmore\":\"LEARN MORE\"\n}"}]